// If you include this script, comment out the main function below
(function() { main(); })();
function main() {
    // Get a current reference point
    var key = 'plugin/Transform/AnchorPoint';
    var position = app.preferences.getIntegerPreference(key);

    // Get an icon of the current reference point
    var icon = getReferencePoint(position);

    // Create a dialog
    var dialog = new Window('dialog');
    dialog.text = 'Reference Point';
    dialog.preferredSize.width = 200;
    dialog.preferredSize.height = 120;
    dialog.alignChildren = ['center', 'center'];

    // Add the reference point icon in the dialog
    var referencePoint = dialog.add('image', undefined, icon, { name: 'RP' });
    referencePoint.addEventListener('click', changeReferencePoint);

    dialog.show();
}


// If you want to copy and paste this UI into your script, copy all the code below from here.
/**
 * Change the reference point.
 * @param {Event} event Interface Event.
 */
function changeReferencePoint(event) {
    var cursor = getCursorPosition(event.clientX, event.clientY);
    var icon = getReferencePoint(cursor);
    if (!icon) return;

    // Replace the Reference Point image
    this.image = ScriptUI.newImage(this.image);
    this.image = ScriptUI.newImage(icon);

    var key = 'plugin/Transform/AnchorPoint';
    app.preferences.setIntegerPreference(key, cursor);
}


/**
 * Get the icon of the reference point.
 * @param {number} position Position of the reference point.
 * @returns {File} Icon image file.
 */
function getReferencePoint(position) {
    var icon = getIcon();
    switch (position) {
        case 0: return icon.TOP_LEFT;
        case 1: return icon.TOP_CENTER;
        case 2: return icon.TOP_RIGHT;
        case 3: return icon.MIDDLE_LEFT;
        case 4: return icon.MIDDLE_CENTER;
        case 5: return icon.MIDDLE_RIGHT;
        case 6: return icon.BOTTOM_LEFT;
        case 7: return icon.BOTTOM_CENTER;
        case 8: return icon.BOTTOM_RIGHT;
    }
}


/**
 * Get the position where the mouse was clicked.
 * @param {number} x X-coordinate of the cursor.
 * @param {number} y Y-coordinate of the cursor.
 * @returns {number} Cursor position.
 */
function getCursorPosition(x, y) {
    var position = {
        TOP_LEFT: 0,
        TOP_CENTER: 1,
        TOP_RIGHT: 2,
        MIDDLE_LEFT: 3,
        MIDDLE_CENTER: 4,
        MIDDLE_RIGHT: 5,
        BOTTOM_LEFT: 6,
        BOTTOM_CENTER: 7,
        BOTTOM_RIGHT: 8
    };
    if (isLeft(x) && isTop(y)) return position.TOP_LEFT;
    if (isCenter(x) && isTop(y)) return position.TOP_CENTER;
    if (isRight(x) && isTop(y)) return position.TOP_RIGHT;
    if (isLeft(x) && isCenter(y)) return position.MIDDLE_LEFT;
    if (isCenter(x) && isCenter(y)) return position.MIDDLE_CENTER;
    if (isRight(x) && isCenter(y)) return position.MIDDLE_RIGHT;
    if (isLeft(x) && isBottom(y)) return position.BOTTOM_LEFT;
    if (isCenter(x) && isBottom(y)) return position.BOTTOM_CENTER;
    if (isRight(x) && isBottom(y)) return position.BOTTOM_RIGHT;
}


/**
 * Position of the cursor on the horizontal axis.
 * @param {number} value X-coordinate of the cursor.
 * @returns {boolean} If true, the cursor is on the left.
 */
function isLeft(value) {
    var min = 1;
    var max = 5;
    return min <= value && value <= max;
}


/**
 * Position of the cursor on the vertical axis.
 * @param {number} value Y-coordinate of the cursor.
 * @returns {boolean} If true, the cursor is on the top.
 */
function isTop(value) {
    var min = 1;
    var max = 5;
    return min <= value && value <= max;
}


/**
 * Position of the cursor on the center.
 * @param {number} value X-coordinate or Y-coordinate of the cursor.
 * @returns {boolean} If true, the cursor is on the center.
 */
function isCenter(value) {
    var min = 8;
    var max = 12;
    return min <= value && value <= max;
}


/**
 * Position of the cursor on the horizontal axis.
 * @param {number} value X-coordinate of the cursor.
 * @returns {boolean} If true, the cursor is on the right.
 */
function isRight(value) {
    var min = 15;
    var max = 19;
    return min <= value && value <= max;
}


/**
 * Position of the cursor on the vertical axis.
 * @param {number} value Y-coordinate of the cursor.
 * @returns {boolean} If true, the cursor is on the bottom.
 */
function isBottom(value) {
    var min = 15;
    var max = 19;
    return min <= value && value <= max;
}


